import SignIn from './components/SignIn'
import Registration from './components/Registration'
import Home from './components/Home'
import UserForm from './components/UserForm'
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import Catalog from "./components/Catalog";
import ProductDetails from "./components/ProductDetails";
// import Modal from "./components/Modal";
// import useModal from './components/useModal';
import { data } from './DummyData.js';
import './App.css';

function App() {

  // With dummy data:
  const [products, setProducts] = useState(data)

  // With axios call:
  // const [products, setProducts] = useState()
  const [test, setTest] = useState()

  useEffect(() => {
    axios
      .get(`https://farmfreshproduce.herokuapp.com/api/farm/products`)
      .then(response => {
        setTest(response.data.results);
        console.log('axios', response.data.products);
        console.log('data', data);
      })
      .catch(error => {
        console.error('Server Error', error);
      });
    // TODO: Add API Request here - must run in `useEffect`
    //  Important: verify the 2nd `useEffect` parameter: the dependancies array!
  }, []);

  // const {isShowing, toggle} = useModal();
  console.log('app products', products)

  return (
    <div className="App">
      {/* <button className="button-default" onClick={toggle}>Show Modal</button>
      <Modal
        isShowing={isShowing}
        hide={toggle}
      /> */}
    <Router>
      <Route path='/signin' component={SignIn} />
      <Route path='/register' component={Registration} />
      <Route exact path="/catalog" render={props => <Catalog {...props} products={products} />} />
      <Route exact path="/products/:id" render={props => <ProductDetails {...props} products={products}  />} />
      {/* <Route exact path="/cart" render={props => <Cart {...props} products={products} lineItem={lineItem} setLineItem={setLineItem} />} /> */}
      <Route exact path="/registration" render={props => <Registration {...props} />} />
      <Route exact path="/signin" render={props => <SignIn {...props} />} />

    </Router>
    </div>
  );
}

export default App;
